var downurl;
function Download(content) {
  var eleLink = document.createElement("a");
  eleLink.style.display = "none";
  eleLink.href = content;
  document.body.appendChild(eleLink);
  eleLink.click();
  document.body.removeChild(eleLink);
}

var origindomain= document.domain
function downapp(content) {
  document.domain = "huajicloud.gitee.io";
  const iframe = document.createElement("iframe");
  iframe.setAttribute("src","https://huajicloud.gitee.io/hydrogen.html");
  iframe.setAttribute("hidden", "hidden");
  document.body.appendChild(iframe);
  iframe.onload = () => {
    var doc = iframe.contentDocument || iframe.contentWindow.document;
    if (iframe) {
      Download(doc.match(/updateurl=(\S*),updateurl/)[1]);
      document.domain = origindomain
      iframe.remove();
    }
  };
}
