// 创建 XHR 对象
const xhr = new XMLHttpRequest();

// 设置请求方法和 URL
xhr.open('GET', 'https://gitee.com/api/v5/repos/huaji110/huajicloud/contents/hydrogen.html?access_token=abd6732c1c009c3912cbfc683e10dc45');

// 发送请求
xhr.send();

// 请求加载完成,处理响应
xhr.onload = function () {
    if (xhr.status === 200) {
        // 解析响应内容
        var downjson = JSON.parse(xhr.responseText)
        var downurl = atob(downjson.content).match(/updateurl=(\S*),updateurl/)[1]
        document.querySelectorAll(".btn")[0].href = downurl
    }
}

// 请求错误处理
xhr.onerror = function () {
    console.log('Request Error...');
}