function Toast(msg, duration) {
    duration = isNaN(duration) ? 3000 : duration;
    var m = document.createElement('div');
    m.innerHTML = msg;
    m.style.cssText = "font-size: .32rem;color: rgb(255, 255, 255);background-color: rgba(0, 0, 0, 0.6);padding: 10px 15px;margin: 0 0 0 -60px;border-radius: 4px;position: fixed;    top: 50%;left: 50%;width: 130px;text-align: center;";
    document.body.appendChild(m);
    setTimeout(function () {
        var d = 0.5;
        m.style.opacity = '0';
        setTimeout(function () { document.body.removeChild(m) }, d * 1000);
    }, duration);
}

function loading() {
    Toast("下载链接正在加载中 请耐心等待", 1000)
}

// 创建 XHR 对象
const xhr = new XMLHttpRequest();

// 设置请求方法和 URL
xhr.open('GET', 'https://huajicloud-data.onrender.com/hydrogen.html');

// 发送请求
xhr.send();

// 请求加载完成,处理响应
xhr.onload = function () {
    if (xhr.status === 200) {
        // 解析响应内容
        var downurl = xhr.responseText.match(/updateurl=(\S*),updateurl/)[1];
        document.querySelectorAll(".btn")[0].href = downurl
    }
}

// 请求错误处理
xhr.onerror = function () {
    console.log('Request Error...');
}